uint8_t Key_Data[300]={0};

//第一步：检查chip id，查看I2c是否接通
int CheckChipId(void)
{
#ifdef LT7211_UPGRADE_DEBUG
	printf("CheckChipId \n");
#endif
	LT_WriteI2C_Byte(0xFF, 0x80);
	LT_WriteI2C_Byte(0xEE, 0x01);

	LT_WriteI2C_Byte(0xff, 0xa0);
	uint8_t bA000 = LT_ReadI2C_Byte(0x00);
	uint8_t bA001 = LT_ReadI2C_Byte(0x01);

	int nChipID = ((bA000 << 8) | bA001);
	return nChipID == 0x1605;
}

//第二步：配置寄存器
void Config(void)
{
#ifdef LT7211_UPGRADE_DEBUG
	printf("Config \n");
#endif
	LT_WriteI2C_Byte( 0xFF, 0x80 );
	LT_WriteI2C_Byte( 0xEE, 0x01 );

	LT_WriteI2C_Byte( 0x5A, 0x82 );
	LT_WriteI2C_Byte( 0x5E, 0xC0 );
	LT_WriteI2C_Byte( 0x58, 0x00 );
	LT_WriteI2C_Byte( 0x59, 0x51 );
	LT_WriteI2C_Byte( 0x5A, 0x92 );
	LT_WriteI2C_Byte( 0x5A, 0x82 );
}

//第三步：将Flash里的HDCP Key读出来保存
void ReadKey(void)
{
	int bRet;
#ifdef LT7211_UPGRADE_DEBUG
	printf("ReadKey \n");
#endif
	LT_WriteI2C_Byte( 0xFF, 0x80 );
	LT_WriteI2C_Byte( 0xEE, 0x01 );

	//读取之前reset fifo
	LT_WriteI2C_Byte( 0xFF, 0x90 );
	uint8_t bRead = LT_ReadI2C_Byte( 0x02 );
	bRead &= 0xDF;
	LT_WriteI2C_Byte( 0x02, bRead );
	bRead |= 0x20;
	LT_WriteI2C_Byte( 0x02, bRead );
	
	//wren enable
	LT_WriteI2C_Byte( 0xFF, 0x80 );
	LT_WriteI2C_Byte( 0x5A, 0x86 );
	LT_WriteI2C_Byte( 0x5A, 0x82 );
	
	long lReadAddr = 0x6000;//Key存储在Flash里的地址是0x6000
	uint8_t addr[3] = { 0, 0, 0 };
	addr[0] = ( lReadAddr & 0xFF0000 ) >> 16;
	addr[1] = ( lReadAddr & 0xFF00 )   >>  8;
	addr[2] = lReadAddr & 0xFF;

	int nPage = 286/ 16;//Key总共286个字节，配置一次最多读写16个字节
	if ( 286 % 16 != 0 )
	{
		++nPage;
	}

	for ( int i=0; i<nPage; ++i )//每次最多读16个字节，总共读npage次
	{
		//设置读取地址
		LT_WriteI2C_Byte( 0x5E, 0x60 | 0xF );
		LT_WriteI2C_Byte( 0x5A, 0xA2 );
		LT_WriteI2C_Byte( 0x5A, 0x82 );
		LT_WriteI2C_Byte( 0x5B, addr[0] );
		LT_WriteI2C_Byte( 0x5C, addr[1] );
		LT_WriteI2C_Byte( 0x5D, addr[2] );
		LT_WriteI2C_Byte( 0x5A, 0x92 );
		LT_WriteI2C_Byte( 0x5A, 0x82 );	
		LT_WriteI2C_Byte( 0x58, 0x01 );

		int nPageReadLen = 16;
		if ( 286 - i * 16 < 16)//最后一次长度没有16个，计算最后一次的数据长度
		{
			nPageReadLen = 286 - i * 16;//nPageReadLen是最后一次的长度
		#ifdef LT7211_UPGRADE_DEBUG
			printf("nPageReadLen = %d \n",nPageReadLen);
		#endif
		}
		
		//将读到的Key数据储存起来
		uint8_t byPageReadData[128];
		memset( byPageReadData, 0, 128 );
		bRet = LT_ReadI2C_ByteN( 0x5F, byPageReadData, nPageReadLen );//从0x5F中读取
		for (int m = 0;m < nPageReadLen; m++)
		{
			memset(Key_Data+(i*16)+m, byPageReadData[m], 1);
		#ifdef LT7211_UPGRADE_DEBUG
			printf("keydata[%d] = %x, readdata[%d] = %x \n",(i*16+m),Key_Data[(i*16)+m],m,byPageReadData[m]);
		#endif
		}
		
		
		//下次读取时地址增加16个
		lReadAddr += nPageReadLen;
		addr[0] = ( lReadAddr & 0xFF0000 ) >> 16;
		addr[1] = ( lReadAddr & 0xFF00 )   >>  8;
		addr[2] = lReadAddr & 0xFF;		
	}
}

//第四步：Block Erase，擦除所有数据
void BlockErase(void)
{
	printf("BlockErase \n");
	LT_WriteI2C_Byte( 0xFF, 0x80 );
	LT_WriteI2C_Byte( 0xEE, 0x01 );
	LT_WriteI2C_Byte( 0x5A, 0x82 );

	LT_WriteI2C_Byte( 0x5A, 0x86 );
	LT_WriteI2C_Byte( 0x5A, 0x82 );

	LT_WriteI2C_Byte( 0x5B, 0x00 );
	LT_WriteI2C_Byte( 0x5C, 0x00 );
	LT_WriteI2C_Byte( 0x5D, 0x00 );

	LT_WriteI2C_Byte( 0x5A, 0x83 );
	LT_WriteI2C_Byte( 0x5A, 0x82 );

	delay_ms( 1000 );
}

//第五步：写入Firmware的数据
void Write_Frimware(void)
{
	int DataLen = sizeof(firmware_data);
	uint8_t *byWriteData = firmware_data;
#ifdef LT7211_UPGRADE_DEBUG
	printf("Write_Frimware DataLen = %d\n",DataLen);
#endif
	LT_WriteI2C_Byte( 0xFF, 0x80 );
	LT_WriteI2C_Byte( 0xEE, 0x01 );

	//写之前reset fifo
	LT_WriteI2C_Byte( 0xFF, 0x90 );
	uint8_t bRead = LT_ReadI2C_Byte( 0x02 );
	bRead &= 0xDF;
	LT_WriteI2C_Byte( 0x02, bRead );
	bRead |= 0x20;
	LT_WriteI2C_Byte( 0x02, bRead );

	//wren enable
	LT_WriteI2C_Byte( 0xFF, 0x80 );
	LT_WriteI2C_Byte( 0x5A, 0x86 );
	LT_WriteI2C_Byte( 0x5A, 0x82 );
	
	//设置Data数据的起始地址
	uint8_t addr[3] = { 0, 0, 0 };
	long lWriteAddr = 0;
	long lStartAddr = lWriteAddr;
	
	int nPage = DataLen/ 16;//Datalen是固件数据的字节数，配置一次最多读写16个字节
	if ( DataLen % 16 != 0 )
	{
		++nPage;
	}

	for ( int j = 0; j < nPage; ++j )
	{
		// 写wren命令(为一个pulse，此时不需要考虑wrrd_mode，spi_paddr[1:0]的值)
		LT_WriteI2C_Byte( 0x5A, 0x86 );
		LT_WriteI2C_Byte( 0x5A, 0x82 );

		// 配置spi_len[3:0]= 15，可配置，spi内部加1，即配置一次写入16个字节
		LT_WriteI2C_Byte( 0x5E, 0xE0 | 0xF );
		LT_WriteI2C_Byte( 0x5A, 0xA2 );
		LT_WriteI2C_Byte( 0x5A, 0x82 );

		LT_WriteI2C_Byte( 0x58, 0x01 );

		LT_WriteI2C_ByteN( 0x59, byWriteData+j*16, 16 );//byWriteData指向固件数据的起始地址，每次写16个，最后不足16个的补FF

		// 把fifo数据写到flash(当wrrd_mode = 1，spi_paddr= 2’b10,addr[23:0](地址在写入过程中需要保持不变)准备好的情况下，给一个spi_sta的pulse，就开始写入flash)
		LT_WriteI2C_Byte( 0x5B, addr[0] );
		LT_WriteI2C_Byte( 0x5C, addr[1] );
		LT_WriteI2C_Byte( 0x5D, addr[2] );
		LT_WriteI2C_Byte( 0x5E, 0xE0 );
		LT_WriteI2C_Byte( 0x5A, 0x92 );
		LT_WriteI2C_Byte( 0x5A, 0x82 );

		lStartAddr += 16;
		addr[0] = ( lStartAddr & 0xFF0000 ) >> 16;
		addr[1] = ( lStartAddr & 0xFF00 ) >> 8;
		addr[2] =   lStartAddr & 0xFF;
	}
	// 写wrdi禁止命令(为一个pulse，此时不需要考虑wrrd_mode，spi_paddr[1:0]的值)
	LT_WriteI2C_Byte( 0x5A, 0x8A );
	LT_WriteI2C_Byte( 0x5A, 0x82 );
}

//第六步：读数据
int Verify_Firmware(void)
{
#ifdef LT7211_UPGRADE_DEBUG
	printf("Verify_Firmware \n");
#endif
	LT_WriteI2C_Byte( 0xFF, 0x80 );
	LT_WriteI2C_Byte( 0xEE, 0x01 );

	//读之前reset fifo
	LT_WriteI2C_Byte( 0xFF, 0x90 );
	uint8_t bRead = LT_ReadI2C_Byte( 0x02 );
	bRead &= 0xDF;
	LT_WriteI2C_Byte( 0x02, bRead );
	bRead |= 0x20;
	LT_WriteI2C_Byte( 0x02, bRead );

	//wren enable
	LT_WriteI2C_Byte( 0xFF, 0x80 );
	LT_WriteI2C_Byte( 0x5A, 0x86 );
	LT_WriteI2C_Byte( 0x5A, 0x82 );

	long lReadAddr = 0; //读数据的起始地址是00
	uint8_t addr[3] = { 0, 0, 0 };
	addr[0] = ( lReadAddr & 0xFF0000 ) >> 16;
	addr[1] = ( lReadAddr & 0xFF00 )   >>  8;
	addr[2] = lReadAddr & 0xFF;

	int WriteDataLen = sizeof(firmware_data);//写入时的数据长度
#ifdef LT7211_UPGRADE_DEBUG
	printf("Verify_Firmware WriteDataLen = %d\n",WriteDataLen);
#endif
	int nPage = WriteDataLen / 16;//Datalen是固件数据的字节数，配置一次最多读写16个字节
	if ( WriteDataLen % 16 != 0 )
	{
		++nPage;
	}

	for ( int i=0; i<nPage; ++i )
	{
		LT_WriteI2C_Byte( 0x5E, 0x60 | 0xF );
		LT_WriteI2C_Byte( 0x5A, 0xA2 );
		LT_WriteI2C_Byte( 0x5A, 0x82 );
		LT_WriteI2C_Byte( 0x5B, addr[0] );
		LT_WriteI2C_Byte( 0x5C, addr[1] );
		LT_WriteI2C_Byte( 0x5D, addr[2] );
		LT_WriteI2C_Byte( 0x5A, 0x92 );
		LT_WriteI2C_Byte( 0x5A, 0x82 );	
		LT_WriteI2C_Byte( 0x58, 0x01 );

		int nPageReadLen = 16;
		if ( WriteDataLen - i * 16 < 16 )//最后一次长度没有16个，计算最后一次的数据长度
		{
			nPageReadLen = WriteDataLen - i * 16;
		#ifdef LT7211_UPGRADE_DEBUG
			printf("nPageReadLen = %d \n",nPageReadLen);
		#endif
		}
		//将读出来的数据和固件数据比较一下
		uint8_t byPageReadData[128];
		memset( byPageReadData, 0, 128 );
		int bRet = LT_ReadI2C_ByteN( 0x5F, byPageReadData, nPageReadLen );
		
		for (int n = 0; n < nPageReadLen; n++)
		{
		#ifdef LT7211_UPGRADE_DEBUG
			printf("ReadData[%d] = %x,firmwareData[%d] = %x \n",n,byPageReadData[n],(i * 16 + n),firmware_data[i * 16 + n]);
		#endif
			if(byPageReadData[n] != firmware_data[i * 16 + n])
			{
				return FALSE;
			}
		}
		
		//下次读取时地址增加16个
		lReadAddr += nPageReadLen;
		addr[0] = ( lReadAddr & 0xFF0000 ) >> 16;
		addr[1] = ( lReadAddr & 0xFF00 )   >>  8;
		addr[2] = lReadAddr & 0xFF;		
	}
	return TRUE;
}


//第七步：将第三步读出来的Key写入
void Write_Key(void)
{
	printf("Write_Key \n");
	LT_WriteI2C_Byte( 0xFF, 0x80 );
	LT_WriteI2C_Byte( 0xEE, 0x01 );

	//写之前reset fifo
	LT_WriteI2C_Byte( 0xFF, 0x90 );
	uint8_t bRead = LT_ReadI2C_Byte( 0x02 );
	bRead &= 0xDF;
	LT_WriteI2C_Byte( 0x02, bRead );
	bRead |= 0x20;
	LT_WriteI2C_Byte( 0x02, bRead );


	uint8_t addr[3] = { 0x00, 0x60, 0x00 };

	LT_WriteI2C_Byte( 0xFF, 0x80 );
	LT_WriteI2C_Byte( 0x5A, 0x86 );
	LT_WriteI2C_Byte( 0x5A, 0x82 );
	
	//读Key的起始地址在0x6000
	long lWriteAddr = 0x6000;
	long lStartAddr = lWriteAddr;
	long lEndAddr   = lWriteAddr;


	int nPage = 286/ 16;//286是HCP Key的字节数，配置一次最多读写16个字节
	if ( 286 % 16 != 0 )
	{
		++nPage;
	}

	for ( int j = 0; j < nPage; ++j )
	{
		// 写wren命令(为一个pulse，此时不需要考虑wrrd_mode，spi_paddr[1:0]的值)
		LT_WriteI2C_Byte( 0x5A, 0x86 );
		LT_WriteI2C_Byte( 0x5A, 0x82 );

		// 配置spi_len[3:0]= 15，可配置，spi内部加1，即配置一次写入16个字节
		LT_WriteI2C_Byte( 0x5E, 0xE0 | 0xF );
		LT_WriteI2C_Byte( 0x5A, 0xA2 );
		LT_WriteI2C_Byte( 0x5A, 0x82 );

		LT_WriteI2C_Byte( 0x58, 0x01 );

		LT_WriteI2C_ByteN( 0x59, Key_Data+j*16, 16 );//byWriteData指向Key数据的起始地址，每次写16个，最后不足16个的补FF

		// 把fifo数据写到flash(当wrrd_mode = 1，spi_paddr= 2’b10,addr[23:0](地址在写入过程中需要保持不变)准备好的情况下，给一个spi_sta的pulse，就开始写入flash)
		LT_WriteI2C_Byte( 0x5B, addr[0] );
		LT_WriteI2C_Byte( 0x5C, addr[1] );
		LT_WriteI2C_Byte( 0x5D, addr[2] );
		LT_WriteI2C_Byte( 0x5E, 0xE0 );
		LT_WriteI2C_Byte( 0x5A, 0x92 );
		LT_WriteI2C_Byte( 0x5A, 0x82 );

		lStartAddr += 16;
		addr[0] = ( lStartAddr & 0xFF0000 ) >> 16;
		addr[1] = ( lStartAddr & 0xFF00 ) >> 8;
		addr[2] =   lStartAddr & 0xFF;
	}
	// 写wrdi禁止命令(为一个pulse，此时不需要考虑wrrd_mode，spi_paddr[1:0]的值)
	LT_WriteI2C_Byte( 0x5A, 0x8A );
	LT_WriteI2C_Byte( 0x5A, 0x82 );

}

void LT7211_Firmware_Upgrade(void)
{
#ifdef LT7211_UPGRADE_DEBUG
	printf("LT7211_Firmware_Upgrade \n");
#endif

	if (CheckChipId())
	{
		Config();
		ReadKey();
		BlockErase();
		Write_Frimware();
		Verify_Firmware();
			
		Write_Key();
		
		LT7211_RESET = 0;
		delay_ms( 200 );
		LT7211_RESET = 1;
	}
}
